import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';

import { Temp } from "./temp"
import { Observable } from 'rxjs';

@Injectable({
    providedIn: 'root'
})
export class ProductsService {

    constructor(private http: HttpClient) { }

    getProducts(): Observable<any> {
        return new Observable((observer) => {
            this.http.get<Temp>("http://localhost:3000/").subscribe({
                next: (data) => {
                    observer.next(data); // Emit the response data
                },
                error: (error) => {
                    observer.error(error); // Emit the error
                },
                complete: () => {
                    observer.complete(); // Complete the observer
                }
            });
        })
        //return this.http.get<Temp>("http://localhost:3000/");
    }
}
